/**
 * @Author {xiheya}
 * @Date: 2022/03/13/ 12:00
 * @Description
 */
public class Student {
//    属性私有
    private String name;        //姓名
    private int id;            //学号
    private char sex;           //性别

    public Student() {
    }

    public Student(String name, int id, char sex) {
        this.name = name;
        this.id = id;
        this.sex = sex;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public int getId() {
        return id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public char getSex() {
        return sex;
    }

    public void setSex(char sex) {
        this.sex = sex;
    }
}
