        int[] arr = {1,3,-2,4,5};
        selectSort(arr);
        for (int i : arr) {
            System.out.print(i + " ");
        }
    }

    public static void selectSort(int[] arr){
        for (int i = 0; i < arr.length - 1; i++) {          //开始选择排序
            //初始 min = arr[i]; mindex = i;
            int min = arr[i];
            int mindex = i;
            for (int j = i + 1; j < arr.length - 1 ; j++){
                //将min与其后面的数比较，如果min大于他后面的数  就更新min，及其下标
                if (min > arr[j]){
                    min = arr[j];
                    mindex = j;
                }
            }
            //如果 最小值的下标不等于 i 则交换 这两个元素的值
            if (mindex != i){
                arr[mindex] = arr[i];
                arr[i] = min;
            }
        }
    }
}
