    }


    public static void mergeSort(int[] arr, int left, int right, int[] temp){
        if (left < right){
            int mid = (left + right) / 2;
            //分
            mergeSort(arr,0,mid,temp);          //将左边部分继续分
            mergeSort(arr,mid+1,right,temp);    //将右边部分继续分
            //合
            merge(arr,left,mid,right,temp);
        }
    }
    //合
    public static void merge(int[] arr, int left, int mid, int right, int[] temp) {
        int i = left;
        int j = mid+1;
        int t = 0;                          //临时数组下标索引
        //先将两部分合并
        while (i <= mid && j <= right){
            if (arr[i] <= arr[j]){
                temp[t] = arr[i];
                i++;t++;
            }else {
                temp[t] = arr[j];
                j++;t++;
            }
        }
        //如果左边没有合并完全，则接着i继续合并
        while (i <= mid){
            temp[t] = arr[i];
            t++;i++;
        }
        //如果右边没有合并完全，则接着j继续合并
        while (j <= right){
            temp[t] = arr[j];
            t++;j++;
        }
        //接着将temp中的数组填充到指定位置
        t = 0;
        int templeft = left;
        while (templeft <= right){
            arr[templeft] = temp[t];
            t++;templeft++;
        }
    }
}
```

