    public static void selectSort(int[] arr){
        for (int i = 0; i < arr.length - 1; i++) {          //
            // min = arr[i]; mindex = i;
            int min = arr[i];
            int mindex = i;
            for (int j = i + 1; j < arr.length - 1 ; j++){
                //minmin  min
                if (min > arr[j]){
                    min = arr[j];
                    mindex = j;
                }
            }
            //  i  
            if (mindex != i){
                arr[mindex] = arr[i];
                arr[i] = min;
            }
        }
    }
}
