package com.xiheya.sort;

/**
 * @Author {xiheya}
 * @Date: 2022/03/16/ 19:34
 * @Description
 */
public class QuickSort {
    public static void main(String[] args) {
        int[] arr ={1,3,-2,4,5,6};
        quickSort(arr,0,arr.length - 1);
        for (int i : arr) {
            System.out.print(i + " ");
        }
    }

    public static void quickSort(int[] arr,int left, int right){
        //
        if (left >= right){
            return;
        }
        //
        int l = left;
        int r = right;
        while (l < r){
            while (l < r && arr[r] >= arr[left])r--;            //arr[left]arr[left]r
            while (l < r && arr[l] <= arr[left])l++;            //arr[left]arr[left]l
            if (l == r){                                        // arr[l]arr[r]  arr[left]
                int temp = arr[l];
                arr[l] = arr[left];
                arr[left] = temp;
            }else{                                              //  
                int temp = arr[r];
                arr[r] = arr[l];
                arr[l] = temp;
            }
        }
        quickSort(arr,left,l-1);                           //
        quickSort(arr,r+1,right);                           //
    }
}

