    // 2.
    private volatile static LazyMan lazyMan;            //lazyManvolatile  
    //     DLC
    public static LazyMan getInstance(){
        if (lazyMan == null){
            synchronized (LazyMan.class){
                if (lazyMan == null){
                    lazyMan = new LazyMan();            //
                    /**
                     *    1.
                     *    2.
                     *    3.
                     *    
                     *
                     *    123
                     *    132
                     *    A 132--- 
                     *    B123ABnullifreturnlazyMan
                     *
                     */
                }
            }
        }

        return lazyMan;
    }
}
